		if (IsDeterministic())
		{
			return UE::Mass::Private::RandomSeedOverride;
		}
		return InSeed;
	}

	int32 GenerateRandomSeed()
	{
		if (IsDeterministic())
		{
			return UE::Mass::Private::RandomSeedOverride;
		}
		return FMath::Rand();
	}
#endif

